import React, { useState } from 'react';

function App() {
  const [instructions, setInstructions] = useState('');
  const [processId, setProcessId] = useState('');
  const [status, setStatus] = useState('Waiting for input...');

  const startProcess = async () => {
    setStatus('Submitting...');
    try {
      const res = await fetch('http://localhost:8080/api/start-process', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ instructions })
      });
      if (!res.ok) throw new Error('Submission failed');
      const data = await res.json();
      setProcessId(data.processId);
      checkStatus(data.processId);
    } catch (err) {
      console.error(err);
      setStatus('Failed to submit.');
    }
  };

  const checkStatus = (pid) => {
    const interval = setInterval(async () => {
      try {
        const res = await fetch(`http://localhost:8080/api/status/${pid}`);
        const data = await res.json();
        setStatus(data.status);
        if (['COMPLETED', 'FAILED'].includes(data.status)) {
          clearInterval(interval);
        }
      } catch (err) {
        console.error(err);
      }
    }, 3000);
  };

  return (
    <div className="hero">
      <div className="overlay">
        <header>
          <img src="/static/bonvoy-logo.png" alt="Bonvoy Logo" className="logo" />
          <h1>Welcome to CodeCrusaders</h1>
          <p>Architecture diagrams, code generation, and reviews at your fingertips.</p>
        </header>

        <main>
          <section className="input-card">
            <textarea
              rows="10"
              placeholder="Describe your system or requirements here..."
              value={instructions}
              onChange={(e) => setInstructions(e.target.value)}
            />
            <button onClick={startProcess}>Kickoff Process</button>
            <p>{processId && `Process ID: ${processId}`}</p>
            <p>Status: {status}</p>
          </section>
        </main>

        <footer>
          <p>&copy; 2025 CodeCrusaders & Bonvoy Collaboration</p>
        </footer>
      </div>
    </div>
  );
}

export default App;
